INSERT INTO `kr_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `icon`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`) VALUES
(1, 'wc-admin-welcome-note', 'info', 'en_US', 'New feature(s)', 'Welcome to the new WooCommerce experience! In this new release you\'ll be able to have a glimpse of how your store is doing in the Dashboard, manage important aspects of your business (such as managing orders, stock, reviews) from anywhere in the interface, dive into your store data with a completely new Analytics section and more!', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-03-01 14:00:31', NULL, 0),
(2, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-03-01 14:00:31', NULL, 0),
(3, 'wc-admin-store-notice-giving-feedback', 'info', 'en_US', 'Avalie sua experiência', 'Se você gostou do WooCommerce Admin, deixe uma avaliação de 5 estrelas. Agradecemos antecipadamente!', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-03-18 18:26:49', NULL, 0),
(4, 'wc-admin-mobile-app', 'info', 'en_US', 'Instale o aplicativo Woo', 'Instale o aplicativo do WooCommerce para gerenciar pedidos, receber notificações de vendas e visualizar as principais métricas - onde quer que você esteja.', 'phone', '{}', 'unactioned', 'woocommerce-admin', '2020-03-18 18:26:49', NULL, 0),
(5, 'wc-admin-facebook-extension', 'info', 'en_US', 'Venda no Facebook', 'Grow your business by targeting the right people and driving sales with Facebook. You can install this free extension now.', 'thumbs-up', '{}', 'unactioned', 'woocommerce-admin', '2020-03-18 18:26:49', NULL, 0),
(6, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Help WooCommerce improve with usage tracking', 'Gathering usage data allows us to improve WooCommerce. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href=\"http://localhost/Karine/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Settings</a> and choose to stop sharing data. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">Read more</a> about what data we collect.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-03-18 18:26:49', NULL, 0),
(7, 'wc-update-db-reminder', 'update', 'en_US', 'WooCommerce database update done', 'A atualização do WooCommerce está concluída. Obrigado por atualizar para a última versão!', 'info', '{}', 'actioned', 'woocommerce-core', '2020-03-19 18:42:22', NULL, 0),
(8, 'wc-admin-store-notice-setting-moved', 'info', 'en_US', 'Procurando pela configuração de notificação da loja?', 'Agora isso pode ser encontrado no personalizador.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-03-18 18:42:23', NULL, 0);
